import React from 'react';
import './Main.css';
import SectionLocation from './SectionLocation';

class Main extends React.Component {

    state = {
        locations: {},
    }

    componentDidMount() {
        fetch('https://t1o352i3j3.execute-api.us-west-2.amazonaws.com/dev/locations')
            .then(response => {
                return response.json();
            }).then(result => {
                this.setState(
                    {
                        locations: result
                    }
                );
            });

    }

    render() {

        return (

            <main className="main-container">
                {Object.keys(this.state.locations).map(key => (
                    <SectionLocation
                        key={key}
                        id={key}
                        details={this.state.locations[key]} 
                    />
                ))}
                <section className="location-list">
                    <h4>Location: {this.locations.long_name}</h4>
                    <img src="./img/example-map.jpg" alt={this.locations.long_name} title={this.locations.long_name} />
