  x: 250,
  y: 250,
  height: target.height,
  width: target.width,
  key: target.ref + 1
});

const MapBackground = () => {
  const [image] = useImage('./EWU-CEB.png');
  return <Image x={150} y={0} scaleX={0.9} scaleY={0.9} image={image} />;
};

const stageWidth = 1000;
const stageHeight = 700;

class Graphics extends Component {
  constructor() {
    super()
    this.state = {
