      x={80}
      y={100}
      width={20}
      height={20}
      fill="red"
      shadowBlur={2}
    />

    <Rect
      x={80}
      y={100}
      width={20}
      height={20}
      fill="red"
      name="draggableRect"
      ref="draggableRect"
      draggable
      onDragStart={this.handleDragStart}
      onDragEnd={this.handleDragEnd}
      shadowBlur={2}
    />

    <Circle x={100} y={70} radius={10} fill="lightgrey" shadowBlur={1} />
    <Circle 
      x={100} 
      y={70} 
      radius={10} 
      fill="lightgrey" 
      draggable 
      onDragStart={this.handleDragStart}
      onDragEnd={this.handleDragEnd}
      shadowBlur={1} 
    />

{this.state.canvas.map(({ height, width, x, y }, key) => ( // like a "for loop", this maps over this.state.canvas objects and pulls out the height, width, x, y properties to be used below
          <Rect
            key={key}
            x={x}
            y={y}
            width={width}
            height={height}
            stroke="grey"
            draggable
            fill="blue"
            shadowOffset={{ x: 5, y: 5 }}
            onDragStart={this.handleDragStart}
            onDragEnd={this.handleDragEnd}
            onClick={this.handleClick}
          />
        ))}

      </>
    )
  }
}


export default ToolBar
