import React, { Component } from 'react';
import ReactDOM from 'react-dom'
import Konva from 'konva';
import { render } from 'react-dom';
import { Stage, Layer, Rect, Text, Circle, Line } from 'react-konva';
import 'bootstrap/dist/css/bootstrap.css';
import { Navbar, Nav, NavDropdown, Form, FormControl, Button, Container, Row, Col } from 'react-bootstrap';
// import 'font-awesome/css/font-awesome.min.css';
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
// import { faCoffee } from '@fortawesome/free-solid-svg-icons'
// import '@fortawesome/fontawesome-svg-core/styles.css' // Import the CSS
// import { faThumbsUp } from '@fortawesome/free-solid-svg-icons'

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee } from '@fortawesome/free-solid-svg-icons'



class ToolBar extends Component {
  componentDidMount() {
    // force redraw after a timeout
    // to make sure that the font is loaded
    // setTimeout is just for demo
    // it is better to use some font observer
    setTimeout(() => {
      this.layer.draw();
    }, 2000);
  }

  render() {

    // const element = <FontAwesomeIcon icon={faCoffee} />
    // ReactDOM.render(element, document.body)

    return (
        // <Layer className='toolbar'>
          <Layer ref={node => this.layer = node}>
    <Rect
      x={10}
      y={30}
      width={120}
      height={650}
      fill="white"
      stroke="lightgrey"
    />
            <Text
            x={10}
            y={30}
            fontFamily="FontAwesome"
            text={'\uf063'}
            draggable
            fill="black"
        />
  <Text text={'\uf063'} draggable fontSize={12} />
  {/* <FontAwesomeIcon icon={faCoffee} /> */}
  <Rect
    x={20}
    y={50}
    width={40}
    height={40}
    fill="lightgrey"
    shadowBlur={1}
  />

  <Rect
    x={20}
    y={50}
    width={40}
    height={40}
    fill="lightgrey"
    draggable
    shadowBlur={1}
  />

  <Circle x={100} y={70} radius={20} fill="lightgrey" shadowBlur={1} />
  <Circle x={100} y={70} radius={20} fill="lightgrey" draggable shadowBlur={1} />

        </Layer>
    )
  }
}


export default ToolBar
