import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Konva from 'konva';
import { render } from 'react-dom';
import { Stage, Layer, Image, Rect, Text, Circle, Line, Transformer } from 'react-konva';
import { Navbar, Nav, NavDropdown, Form, FormControl, Button, Container, Row, Col } from 'react-bootstrap';
import useImage from 'use-image';
import ToolBar from './toolbar';



const stageWidth = 1000;
const stageHeight = 700;

const LionImageD = () => {
  const [image] = useImage('https://konvajs.org/assets/lion.png');
  return <Image x={10} y={90} scaleX={0.4} scaleY={0.4} draggable image={image} />;
};

const LionImage = () => {
  const [image] = useImage('https://konvajs.org/assets/lion.png');
  return <Image x={10} y={90} scaleX={0.4} scaleY={0.4} image={image} />;
};

const MapBackground = () => {
  const [image] = useImage('/EWU-CEB.png');
  return <Image draggable image={image} />;
};

class Graphics extends Component {

  constructor() {
    super()
    this.state = {}
  }

  render() {
    return (
    <div className="graphics">
    <Stage width={stageWidth} height={stageHeight} ref="bigStage">

      <Layer><MapBackground /></Layer>

      <Layer>

      {/* <Image src='/EWU-CEB.png' fluid /> */}

        <LionImage />
        <LionImageD />


      </Layer>
    </Stage>
  </div>
    )
  }
}


export default Graphics;
